SELECT * FROM company_name AS cn, company_type AS ct, movie_companies AS mc, title AS t WHERE cn.cn1_country_code ='[us]' AND (ct.ct_kind ='production companies' or ct.ct_kind = 'distributors') AND t.t_production_year >2000 AND t.t_id = mc.mc_movie_id AND ct.ct_id = mc.mc_company_type_id AND cn.cn1_id = mc.mc_company_id;
